import AddTodo from "./AddTodo";
import store from "./store";
import { observer } from "mobx-react";
import styled from "styled-components";

const Checkbox = styled.input`
  margin-top: 20px;
  margin-right: 10px;
`;

const Headline = styled.div`
    margin-top: 10px;
`

const TodoList = () => {
  return (
    <div className="column todo-list">
      <div className="todo-list__header">Todo List</div>
      <Headline>You have {store.unfinishedTodos} unfinished todos left.</Headline>
      <div>
        {store.todos.map((todo) => {
          return (
            <div key={todo.id} className="row">
              <Checkbox
                type="checkbox"
                checked={todo.done}
                onClick={() => (todo.done = !todo.done)}
              />
              <input
                className="input"
                type="text"
                value={todo.text}
                onChange={(e) => (todo.text = e.target.value)}
              />
              <button
                className="button"
                onClick={() => store.removeTodo(todo.id)}
              >
                Delete
              </button>
            </div>
          );
        })}
      </div>
      <AddTodo />
    </div>
  );
};

export default observer(TodoList);
