import { observer } from "mobx-react";
import { useState } from "react";

import store from "../../store/store";

import { Input } from "./add-todo.styles";

const AddTodo = () => {
  const [error, setError] = useState("");

  return (
    <div className="row">
      <Input
        className="input"
        type="text"
        placeholder="New Todo"
        value={store.newTodo}
        onChange={(e) => (store.newTodo = e.target.value)}
      />
      <button
        className="button"
        onClick={() => {
          if (store.newTodo !== "") {
            store.addTodo();
          } else {
            setError("Bitte gib einen Text ein");
          }
        }}
      >
        Add Todo
      </button>
    </div>
  );
};

export default observer(AddTodo);
